export default {
  name: "App",
  components: {},

  created() {
    this.socket.on("tips", msg => {
      this.$notify(msg);

      if (msg.detail === "createRoom") {
        //通知tips组件
        this.$bus.$emit("createRoom", msg.detail);
      }

      if (msg.detail === "addRoom") {
        window.setTimeout(() => {
          //通知Desk组件
          this.$bus.$emit('addRoom', msg.playerData);
        }, 2500);
      }
    });
  }

};
